function startQuiz(difficulty) {
  // ë‚œì´ë„ë³„ 30ë¬¸ì œ ë°ì´í„° ì¤‘ 10ë¬¸í•­ ëœë¤ ì¶”ì¶œ
  if (difficulty === 'easy') {
    currentQuizData = getRandomTen(quizDataEasy);
  } else if (difficulty === 'medium') {
    currentQuizData = getRandomTen(quizDataMedium);
  } else if (difficulty === 'hard') {
    currentQuizData = getRandomTen(quizDataHard);
  }

  // ë³€ìˆ˜ ì´ˆê¸°í™”
  currentQuestionIndex = 0;
  score = 0;

  // ë‚œì´ë„ ì„ íƒ ë°•ìŠ¤ ìˆ¨ê¸°ê³ , ë¬¸ì œ í‘œì‹œ ì˜ì—­ ë³´ì´ê¸°
  document.getElementById('difficultySelectBox').style.display = 'none';
  // ì§„í–‰ ë°”ì™€ ë¬¸ì œ ë°•ìŠ¤ ë³´ì´ê¸°
  document.getElementById('progressBarContainer').style.display = 'block';
  document.getElementById('questionBox').style.display = 'block';

  // ì²« ë¬¸ì œ í‘œì‹œ
  showQuestion();
}


/************************************************************
 * ëœë¤ 10ë¬¸í•­ ì¶”ì¶œ í•¨ìˆ˜
 ************************************************************/
function getRandomTen(fullArray) {
  // shallow copy í›„ shuffle, ì•ì—ì„œ 10ê°œ slice
  let copied = [...fullArray];
  shuffleArray(copied);
  return copied.slice(0, 10);
}

// í”¼ì…”â€“ì˜ˆì´ì¸ ( Fisher-Yates ) ì…”í”Œ ì•Œê³ ë¦¬ì¦˜
function shuffleArray(arr) {
  for (let i = arr.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [arr[i], arr[j]] = [arr[j], arr[i]];
  }
}

/************************************************************
 * updateProgressBar: ì§„í–‰ ë°” width, í…ìŠ¤íŠ¸ ê°±ì‹ 
 ************************************************************/
function updateProgressBar() {
  const progressBar = document.getElementById('progressBar');
  const progressText = document.getElementById('progressText');

  const total = currentQuizData.length;  // ë³´í†µ 10
  const current = currentQuestionIndex + 1; // 1-based index

  // ì§„í–‰ ë¹„ìœ¨ ê³„ì‚°
  const progressPercent = (current / total) * 100;

  // ë°” ë„ˆë¹„ ì„¤ì • (0% ~ 100%)
  progressBar.style.width = progressPercent + "%";

  // í…ìŠ¤íŠ¸: "í˜„ì¬ / ì „ì²´"
  progressText.textContent = `${current} / ${total}`;
}


